import React, { useState } from 'react';
import './ExpenseForm.css';
const ExpenseForm = () => {
    //here we are using multiple states which is also correct but if we have to use only one state then taht is below
    //const [titleChanged,setTitleChanged]=useState('');
    //const [dateChanged,setDateChanged]=useState('');
    //const [amountChanged,setAmountChanged]=useState('');

    //using only one state to update the 3 values
    const [userInput,setUserInput]=useState({
        enteredTitle:'',
        enteredAmount:'',
        enteredDate:'',
    });
    const titleChangeHandler = (event) => {
        //setTitleChanged(event.target.value);

        setUserInput({
            ...userState,
            enteredTitle:event.target.value
        })
    };

    const dateChangeHandler = (event) => {
        //setDateChanged(event.target.value);

        setUserInput({
            ...userState,
            enteredDate:event.target.value
        })
    }

    const amountChangeHandler = (event) => {
        //setAmountChanged(event.target.value);

        setUserInput({
            ...userState,
            enteredAmount:event.target.value
        })
    }
    return(
        <form>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min="0.01" step="0.01" onChange={amountChangeHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
};
export default ExpenseForm;
